
public class Exercicio_07 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int var1 = 10;

		System.out.println(var1);
		System.out.println(var1++);
		System.out.println(++var1);

		int var2 = 100;

		System.out.println(var2);
		System.out.println(--var2);
		System.out.println(var2--);
	}

}
